sourcesContent:
import { ObjectId } from 'mongodb';
import dbClient from '../utils/db';
import redisClient from '../utils/redis';

const sha1 = require('sha1');

export default class UsersController {
  static async postNew(req, res) {
    try {
      const userEmail = req.body.email;
      if (!userEmail) {
        return res.status(400).send({
          error: 'Missing email',
        });
      }

      const userPassword = req.body.password;
      if (!userPassword) {
        return res.status(400).send({
          error: 'Missing password',
        });
      }

      let existingEmail = await dbClient.db.collection('users').findOne({
        email: userEmail
      });
      if (existingEmail) {
        return res.status(400).send({
          error: 'Already exist',
        });
      }

      let userId;
      const hashedPw = sha1(userPassword);
      const newUser = {
        email: userEmail,
        password: hashedPw,
      };

      try {
        await dbClient.db.collection('users').insertOne(newUser, (err) => {
          userId = newUser._id;
          return res.status(201).send({
            email: userEmail,
            id: userId,
          });
        });
      } catch (err) {
        return res.status(err.status).send({
          'error': err,
        });
      }

    } catch (error) {
      return res.status(500).send({
        error: 'Server error',
      });
    }
  }

  static async getMe(request, response) {
    const token = request.get('X-Token');
    if (token !== undefined) {
      const usrId = await redisClient.get(`auth_${token}`);
      if (usrId !== null) {
        const usr = await dbClient.database.collection('users')
          .findOne({
            _id: mongo.ObjectID(usrId)
          });
        response.status(200).json({
          id: usrId,
          email: usr.email
        }, );
      } else response.status(401).json({
        error: 'Unauthorized'
      });
    } else response.status(401).json({
      error: 'Unauthorized'
    });
  }
}

